import React from "react";
import App from '../App';

class Login extends React.Component {
    constructor(props) {

        super(props);

        this.state = {
            loginSuccess: false,
            user: null,
            profile: null,
        }
    }
    onSignIn(googleUser) {
        var profile = googleUser.getBasicProfile();
        console.log('ID: ' + profile.getId()); // Do not send to your backend! Use an ID token instead.
        console.log('Name: ' + profile.getName());
        console.log('Image URL: ' + profile.getImageUrl());
        console.log('Email: ' + profile.getEmail()); // This is null if the 'email' scope is not present.
    }
    loadProfile(response) {
        console.log('componentDidUpdate');
        if (response) {

            fetch(`https://www.googleapis.com/oauth2/v1/userinfo?access_token=${response.access_token}`, {
                headers: {
                    Authorization: `Bearer ${response.access_token}`,
                    Accept: 'application/json'
                }
            })
                .then((res) => {
                    console.log(res);
                    this.setState({ profile: res.data });
                })
                .catch((err) => console.log(err));
        }
    }
    render() {
        return <div>
            {this.state.loginSuccess ? null : <div><div id="g_id_onload"
                data-client_id="783266013005-sgk9ngsqg751u5mfpl6umti083pmc4ib"
                data-context="signin"
                data-ux_mode="redirect"
                data-login_uri="http://localhost:3000/"
                data-itp_support="true">
            </div>

                <div class="g_id_signin"
                    data-type="standard"
                    data-shape="rectangular"
                    data-theme="outline"
                    data-text="signin_with"
                    data-size="large"
                    data-locale="en-US"
                    data-logo_alignment="left">
                </div></div>}
            {this.state.loginSuccess ? <App /> : null}
        </div>
    }
}


export default Login;
